// [2,7,11,15] target=9

const twoSums = (arr, target) => {
  let storage = {}
  console.log(arr)
  for (let i = 0; i < arr.length; i++){
    // t - arr[i] is in table then we have our answer
    // else add it to the table
    let keyCheck = target - arr[i]
    if(storage[keyCheck] !== undefined){
      return [storage[keyCheck], i]
    } else {
      storage[keyCheck] = i;
    }
  }
}

export default twoSums;
